/**
 * HR Domain Server for VALEO NeuroERP 3.0
 * Fastify server with OpenAPI documentation
 */

import Fastify from 'fastify';
import swagger from '@fastify/swagger';
import swaggerUi from '@fastify/swagger-ui';
import cors from '@fastify/cors';
// import postgres from 'postgres';

// Import middleware
import { authMiddleware, initializeAuth } from './middleware/auth';

// Import routes
import { registerEmployeeRoutes } from './routes/employees';
import { registerTimeEntryRoutes } from './routes/time-entries';

// Import services
import { EmployeeService } from '../domain/services/employee-service';
import { TimeEntryService } from '../domain/services/time-entry-service';

// Import repositories
import { PostgresEmployeeRepository } from '../infra/repo/postgres-employee-repository';

// Import database schema

// Configuration
const PORT = Number(process.env.PORT || 3030);
const HOST = process.env.HOST || '0.0.0.0';

// Initialize database connection (placeholder)
// const sql = postgres(POSTGRES_URL);
// const db = drizzle(sql, { schema });
const db = {} as any; // Placeholder for now

// Initialize repositories
const employeeRepository = new PostgresEmployeeRepository(db);
// TODO: Initialize other repositories (TimeEntryRepository, etc.)

// Event publisher (placeholder - would connect to NATS/Kafka)
const eventPublisher = async (event: any) => {
  console.log('üì§ Publishing domain event:', event.eventType, event.eventId);
  // TODO: Implement actual event publishing to NATS/Kafka
};

// Initialize services
const employeeService = new EmployeeService(employeeRepository, eventPublisher);
const timeEntryService = new TimeEntryService(
  {} as any, // TODO: Initialize TimeEntryRepository
  employeeRepository,
  eventPublisher
);

// Create Fastify instance
const fastify = Fastify({
  logger: {
    level: process.env.LOG_LEVEL || 'info',
    transport: process.env.NODE_ENV === 'development' ? {
      target: 'pino-pretty',
      options: {
        colorize: true
      }
    } : undefined
  }
});

// Register plugins
async function registerPlugins() {
  // CORS
  await fastify.register(cors, {
    origin: true,
    credentials: true
  });

  // Swagger/OpenAPI
  await fastify.register(swagger, {
    openapi: {
      info: {
        title: 'VALEO NeuroERP 3.0 - HR Domain API',
        description: 'Human Resources Domain API for employee management, time tracking, and payroll preparation',
        version: '1.0.0'
      },
      servers: [
        {
          url: `http://${HOST}:${PORT}`,
          description: 'HR Domain Server'
        }
      ],
      components: {
        securitySchemes: {
          bearerAuth: {
            type: 'http',
            scheme: 'bearer',
            bearerFormat: 'JWT'
          }
        }
      },
      security: [
        {
          bearerAuth: []
        }
      ]
    }
  });

  await fastify.register(swaggerUi, {
    routePrefix: '/docs',
    uiConfig: {
      docExpansion: 'full',
      deepLinking: false
    },
    uiHooks: {
      onRequest: function (_request, _reply, next) {
        next();
      },
      preHandler: function (_request, _reply, next) {
        next();
      }
    },
    staticCSP: true,
    transformStaticCSP: (header) => header,
    transformSpecification: (swaggerObject) => {
      return swaggerObject;
    },
    transformSpecificationClone: true
  });
}

// Register middleware
async function registerMiddleware() {
  // Initialize authentication
  await initializeAuth();

  // Add authentication middleware to all routes
  fastify.addHook('onRequest', authMiddleware);
}

// Register routes
async function registerRoutes() {
  // Health check endpoints
  fastify.get('/health', {}, async () => {
    return {
      ok: true,
      service: 'hr-domain',
      timestamp: new Date().toISOString(),
      uptime: process.uptime()
    };
  });

  fastify.get('/ready', {}, async () => {
    // TODO: Test database connection when implemented
    return {
      ok: true,
      database: true,
      timestamp: new Date().toISOString()
    };
  });

  fastify.get('/live', {}, async () => {
    return {
      ok: true,
      timestamp: new Date().toISOString()
    };
  });

  // Register domain routes
  registerEmployeeRoutes(fastify, employeeService);
  registerTimeEntryRoutes(fastify, timeEntryService);
  // TODO: Register other domain routes (shifts, leaves, payroll, roles)
}

// Error handler
fastify.setErrorHandler(async (error, request, reply) => {
  fastify.log.error(error);

  // Handle validation errors
  if (error.validation) {
    return reply.code(400).send({
      error: 'Validation failed',
      details: error.validation
    });
  }

  // Handle authentication errors
  if (error.statusCode === 401) {
    return reply.code(401).send({
      error: 'Unauthorized',
      message: error.message
    });
  }

  // Handle authorization errors
  if (error.statusCode === 403) {
    return reply.code(403).send({
      error: 'Forbidden',
      message: error.message
    });
  }

  // Handle not found errors
  if (error.statusCode === 404) {
    return reply.code(404).send({
      error: 'Not found',
      message: error.message
    });
  }

  // Default error response
  return reply.code(500).send({
    error: 'Internal server error',
    message: process.env.NODE_ENV === 'development' ? error.message : 'An unexpected error occurred'
  });
});

// Graceful shutdown
async function gracefulShutdown() {
  console.log('üîÑ Gracefully shutting down...');

  try {
    await fastify.close();
    // await sql.end(); // TODO: Close database connection when implemented
    console.log('‚úÖ Server shut down successfully');
    process.exit(0);
  } catch (error) {
    console.error('‚ùå Error during shutdown:', error);
    process.exit(1);
  }
}

// Handle shutdown signals
process.on('SIGTERM', gracefulShutdown);
process.on('SIGINT', gracefulShutdown);

// Start server
async function start() {
  try {
    console.log('üöÄ Starting VALEO NeuroERP 3.0 HR Domain Server...');

    // Register everything
    await registerPlugins();
    await registerMiddleware();
    await registerRoutes();

    // Start listening
    await fastify.listen({ port: PORT, host: HOST });

    console.log(`‚úÖ HR Domain Server running on http://${HOST}:${PORT}`);
    console.log(`üìö API Documentation: http://${HOST}:${PORT}/docs`);
    console.log(`‚ù§Ô∏è  Health Check: http://${HOST}:${PORT}/health`);
    console.log(`üîß Ready Check: http://${HOST}:${PORT}/ready`);
    console.log(`üíì Live Check: http://${HOST}:${PORT}/live`);

  } catch (error) {
    console.error('‚ùå Failed to start server:', error);
    process.exit(1);
  }
}

// Start the server
if (require.main === module) {
  start();
}

export default fastify;

/**
 * Simple HR Domain Server for VALEO NeuroERP 3.0
 * Simplified version without complex Fastify types
 */

import Fastify from 'fastify';
import { EmployeeService } from '../domain/services/employee-service';
import { PostgresEmployeeRepository } from '../infra/repo/postgres-employee-repository';

// Configuration
const PORT = Number(process.env.PORT || 3030);
const HOST = process.env.HOST || '0.0.0.0';

// Initialize services
const employeeRepository = new PostgresEmployeeRepository();
const eventPublisher = async (event: any) => {
  console.log('üì§ Publishing domain event:', event.eventType, event.eventId);
};
const employeeService = new EmployeeService(employeeRepository, eventPublisher);

// Create Fastify instance
const fastify = Fastify({
  logger: {
    level: process.env.LOG_LEVEL || 'info',
    transport: process.env.NODE_ENV === 'development' ? {
      target: 'pino-pretty',
      options: {
        colorize: true
      }
    } : undefined
  }
});

// Health check endpoints
fastify.get('/health', async () => {
  return {
    ok: true,
    service: 'hr-domain',
    timestamp: new Date().toISOString(),
    uptime: process.uptime()
  };
});

fastify.get('/ready', async () => {
  return {
    ok: true,
    database: true,
    timestamp: new Date().toISOString()
  };
});

fastify.get('/live', async () => {
  return {
    ok: true,
    timestamp: new Date().toISOString()
  };
});

// Employee routes (simplified)
fastify.post('/hr/api/v1/employees', async (request, reply) => {
  try {
    const body = request.body as any;
    const employee = await employeeService.createEmployee({
      tenantId: 'default-tenant', // TODO: Get from auth
      createdBy: 'system', // TODO: Get from auth
      ...body
    });
    return reply.code(201).send(employee);
  } catch (error) {
    console.error('Error creating employee:', error);
    return reply.code(400).send({ error: error instanceof Error ? error.message : 'Failed to create employee' });
  }
});

fastify.get('/hr/api/v1/employees/:id', async (request, reply) => {
  try {
    const params = request.params as { id: string };
    const employee = await employeeService.getEmployee('default-tenant', params.id);
    return reply.send(employee);
  } catch (error) {
    console.error('Error getting employee:', error);
    return reply.code(404).send({ error: error instanceof Error ? error.message : 'Employee not found' });
  }
});

fastify.get('/hr/api/v1/employees', async (_request, reply) => {
  try {
    const employees = await employeeService.listEmployees('default-tenant');
    return reply.send(employees);
  } catch (error) {
    console.error('Error listing employees:', error);
    return reply.code(500).send({ error: 'Failed to list employees' });
  }
});

fastify.get('/hr/api/v1/employees/statistics', async (_request, reply) => {
  try {
    const statistics = await employeeService.getEmployeeStatistics('default-tenant');
    return reply.send(statistics);
  } catch (error) {
    console.error('Error getting employee statistics:', error);
    return reply.code(500).send({ error: 'Failed to get employee statistics' });
  }
});

// Error handler
fastify.setErrorHandler(async (error, _request, reply) => {
  fastify.log.error(error);
  return reply.code(500).send({
    error: 'Internal server error',
    message: process.env.NODE_ENV === 'development' ? error.message : 'An unexpected error occurred'
  });
});

// Graceful shutdown
async function gracefulShutdown() {
  console.log('üîÑ Gracefully shutting down...');
  try {
    await fastify.close();
    console.log('‚úÖ Server shut down successfully');
    process.exit(0);
  } catch (error) {
    console.error('‚ùå Error during shutdown:', error);
    process.exit(1);
  }
}

// Handle shutdown signals
process.on('SIGTERM', gracefulShutdown);
process.on('SIGINT', gracefulShutdown);

// Start server
async function start() {
  try {
    console.log('üöÄ Starting VALEO NeuroERP 3.0 HR Domain Server...');
    
    await fastify.listen({ port: PORT, host: HOST });
    
    console.log(`‚úÖ HR Domain Server running on http://${HOST}:${PORT}`);
    console.log(`‚ù§Ô∏è  Health Check: http://${HOST}:${PORT}/health`);
    console.log(`üîß Ready Check: http://${HOST}:${PORT}/ready`);
    console.log(`üíì Live Check: http://${HOST}:${PORT}/live`);
    
  } catch (error) {
    console.error('‚ùå Failed to start server:', error);
    process.exit(1);
  }
}

// Start the server
if (require.main === module) {
  start();
}

export default fastify;
